CN//GUATEMALA CLIENTE NUEVO

var btnAbrirPopupCN = document.getElementById('btn-guat'),
  overlayCN = document.getElementById('overlay'),
  popupCN = document.getElementById('popup'),
  btnCerrarPopupCN = document.getElementById('btn-cerrar-popup');

btnAbrirPopupCN.addEventListener('click', function() {
  overlayCN.classList.add('active')
  popupCN.classList.add('active')
})

btnCerrarPopupCN.addEventListener('click', function() {
  overlayCN.classList.remove('active')
  popupCN.classList.remove('active')
})

