//GUATEMALA MAPA FOOTER

var btnAbrirPopupEM = document.getElementById('mapa-guat'),
  overlayEM = document.getElementById('overlay'),
  popupEM = document.getElementById('popup'),
  btnCerrarPopupEM = document.getElementById('btn-cerrar-popup');

btnAbrirPopupEM.addEventListener('click', function() {
  overlayEM.classList.add('active')
  popupEM.classList.add('active')
})

btnCerrarPopupEM.addEventListener('click', function() {
  overlayEM.classList.remove('active')
  popupEM.classList.remove('active')
})

